import React, { Component } from 'react'
import axios from 'axios'

const SteamContext = React.createContext()
const steamAxios = axios.create()

steamAxios.interceptors.request.use((config) => {
    const token = localStorage.getItem("token")
    config.headers.Authorization = `Bearer ${token}`
    return config
})


class SteamProvider extends Component {
    constructor(){
        super()
        this.state = {
            displayName : ""
        }
    }

    displayName = () => {
        steamAxios.get(("/api/steam/displayName")).then((response)=>{
                console.log(response.data)
                this.setState({displayName : response.data});
            }
        )
    }

    render(){
        return(
            <SteamContext.Provider 
                    value={{
                        ...this.state,
                        displayName: this.displayName
                    }}>
                    { this.props.children }
                </SteamContext.Provider>
        )
    }
}

export default SteamProvider

export const withSteam = C => props => (
    <SteamContext.Consumer>
        { value => <C {...value} {...props}/> }
    </SteamContext.Consumer>
)
