import React from 'react'
import axios from 'axios'

const HomeLayout = props => {
    const {steamID} = props
    
            const summary = (steamID) =>{
                axios.get(`http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v0002/?key=37314FFEEC79310727026EBF2DB722E8&steamids=${steamID}`)
            }
            let name = summary(steamID).response.players[personaname]
       
        
        return (
            <div>
                <p>{name}</p>
            </div>
        )  
}

export default HomeLayout



